String.prototype.lpad = function (pv) {
	   return String(pv + this).slice(-1*Math.max(this.length, pv.length));
};

Number.prototype.lpad = function(pv){
	  return String(pv + this.toString()).slice(-1*Math.max(this.toString().length, pv.length));
};

module.exports = {
	ymd:function(){
		var _z = new Date();
		var y = _z.getFullYear();
		var m = _z.getMonth();
		var d = _z.getDate();
		if(arguments.length === 3){
			var a = arguments;
			y = a[0];
			m = a[1];
			d = a[2];
		}else if(arguments[0] instanceof Date){
			var x = arguments[0];
			y = x.getFullYear();
			m = x.getMonth();
			d = x.getDate();
		}
		return parseInt(y.lpad("0000") + (m+1).lpad("00") + d.lpad("00"));
	},
	toDate:function(){
		var e = arguments[0];
		var y = Math.floor(e/10000);
		var m = Math.floor(e%10000/100) -1;
		var d = e%100;
		return new Date(y,m,d);
	}
};
